import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import axios from 'axios';

// Replace with your actual Google Apps Script Web App URL
const GOOGLE_SCRIPT_URL = 'https://script.google.com/macros/s/AKfycbyoxy5Lf0r4iOyZs2-wKclWk4sCWam8Kg556qVAr-jV9mU7saMn6GUO8ufYQZhhsyE_jw/exec';

// Styled Components
const Container = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 20px;
  font-family: 'Arial', sans-serif;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  min-height: 100vh;
`;

const Header = styled.div`
  text-align: center;
  color: white;
  margin-bottom: 30px;
`;

const Title = styled.h1`
  font-size: 2.5rem;
  margin-bottom: 10px;
  text-shadow: 2px 2px 4px rgba(0,0,0,0.3);
`;

const Subtitle = styled.p`
  font-size: 1.1rem;
  opacity: 0.9;
`;

const Card = styled.div`
  background: white;
  border-radius: 15px;
  padding: 30px;
  margin: 20px 0;
  box-shadow: 0 10px 30px rgba(0,0,0,0.2);
  transition: transform 0.3s ease;

  &:hover {
    transform: translateY(-5px);
  }
`;

const InputSection = styled.div`
  margin-bottom: 30px;
`;

const InputGroup = styled.div`
  display: flex;
  gap: 10px;
  align-items: center;
  margin-bottom: 20px;
`;

const Input = styled.input`
  flex: 1;
  padding: 12px 15px;
  border: 2px solid #e0e0e0;
  border-radius: 8px;
  font-size: 16px;
  transition: border-color 0.3s ease;

  &:focus {
    outline: none;
    border-color: #667eea;
  }
`;

const Button = styled.button`
  padding: 12px 25px;
  background: linear-gradient(45deg, #667eea, #764ba2);
  color: white;
  border: none;
  border-radius: 8px;
  font-size: 16px;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    transform: scale(1.05);
    box-shadow: 0 5px 15px rgba(0,0,0,0.2);
  }

  &:disabled {
    opacity: 0.6;
    cursor: not-allowed;
    transform: none;
  }
`;

const BalanceCard = styled(Card)`
  background: linear-gradient(45deg, #4CAF50, #45a049);
  color: white;
  text-align: center;
`;

const BalanceAmount = styled.div`
  font-size: 3rem;
  font-weight: bold;
  margin: 10px 0;
  text-shadow: 2px 2px 4px rgba(0,0,0,0.3);
`;

const StudentInfo = styled.div`
  background: #f8f9fa;
  padding: 20px;
  border-radius: 10px;
  margin: 20px 0;
`;

const InfoRow = styled.div`
  display: flex;
  justify-content: space-between;
  margin: 10px 0;
  padding: 5px 0;
  border-bottom: 1px solid #e0e0e0;

  &:last-child {
    border-bottom: none;
  }
`;

const Label = styled.span`
  font-weight: bold;
  color: #666;
`;

const Value = styled.span`
  color: #333;
`;

const TransactionHistory = styled.div`
  margin-top: 30px;
`;

const TransactionItem = styled.div`
  background: #f8f9fa;
  padding: 15px;
  margin: 10px 0;
  border-radius: 8px;
  border-left: 4px solid ${props => 
    props.type === 'Recharge' ? '#4CAF50' : 
    props.type === 'Bus Board' ? '#2196F3' : 
    props.type === 'Cafe Payment' ? '#FF5722' : 
    props.type === 'Attendance' ? '#9C27B0' : '#666'
  };
`;

const TransactionHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 5px;
`;

const TransactionType = styled.span`
  font-weight: bold;
  color: ${props => 
    props.type === 'Recharge' ? '#4CAF50' : 
    props.type === 'Bus Board' ? '#2196F3' : 
    props.type === 'Cafe Payment' ? '#FF5722' : 
    props.type === 'Attendance' ? '#9C27B0' : '#666'
  };
`;

const TransactionAmount = styled.span`
  font-weight: bold;
  color: ${props => props.amount > 0 ? '#4CAF50' : props.amount < 0 ? '#f44336' : '#666'};
`;

const TransactionDate = styled.span`
  font-size: 0.9rem;
  color: #666;
`;

const ErrorMessage = styled.div`
  background: #ffebee;
  color: #c62828;
  padding: 15px;
  border-radius: 8px;
  margin: 10px 0;
  border-left: 4px solid #f44336;
`;

const LoadingMessage = styled.div`
  text-align: center;
  padding: 20px;
  color: #666;
  font-style: italic;
`;

function App() {
  const [cardUid, setCardUid] = useState('');
  const [studentData, setStudentData] = useState(null);
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const fetchStudentData = async () => {
    if (!cardUid.trim()) {
      setError('Please enter a valid Card UID');
      return;
    }

    setLoading(true);
    setError('');
    setStudentData(null);
    setTransactions([]);

    try {
      // Fetch student data
      const studentResponse = await axios.get(GOOGLE_SCRIPT_URL, {
        params: {
          action: 'get_student_data',
          card_uid: cardUid.trim()
        }
      });

      if (studentResponse.data.error) {
        setError(studentResponse.data.error);
        return;
      }

      setStudentData(studentResponse.data);

      // Fetch transaction history
      const transactionResponse = await axios.get(GOOGLE_SCRIPT_URL, {
        params: {
          action: 'get_student_transactions',
          card_uid: cardUid.trim()
        }
      });

      if (transactionResponse.data.success) {
        setTransactions(transactionResponse.data.transactions || []);
      }

    } catch (err) {
      setError('Failed to fetch data. Please check your connection and try again.');
      console.error('Error:', err);
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString();
  };

  const formatAmount = (amount) => {
    if (amount === 0) return 'â€”';
    return amount > 0 ? `+$${amount}` : `-$${Math.abs(amount)}`;
  };

  return (
    <Container>
      <Header>
        <Title>ðŸŽ“ Student Balance Portal</Title>
        <Subtitle>RFID Project - Check Your Account Balance</Subtitle>
      </Header>

      <Card>
        <InputSection>
          <h3>Enter Your Card UID</h3>
          <InputGroup>
            <Input
              type="text"
              placeholder="Enter your RFID card UID (e.g., AB12CD34EF)"
              value={cardUid}
              onChange={(e) => setCardUid(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && fetchStudentData()}
            />
            <Button onClick={fetchStudentData} disabled={loading}>
              {loading ? 'Loading...' : 'Check Balance'}
            </Button>
          </InputGroup>
        </InputSection>

        {error && <ErrorMessage>{error}</ErrorMessage>}

        {loading && <LoadingMessage>Loading your account information...</LoadingMessage>}

        {studentData && (
          <>
            <BalanceCard>
              <h2>ðŸ’° Current Balance</h2>
              <BalanceAmount>${studentData.balance}</BalanceAmount>
              <p>Available for spending</p>
            </BalanceCard>

            <StudentInfo>
              <h3>ðŸ“‹ Account Information</h3>
              <InfoRow>
                <Label>Student ID:</Label>
                <Value>{studentData.studentId}</Value>
              </InfoRow>
              <InfoRow>
                <Label>Card UID:</Label>
                <Value>{studentData.cardUid}</Value>
              </InfoRow>
              <InfoRow>
                <Label>Registration Date:</Label>
                <Value>{formatDate(studentData.registrationDate)}</Value>
              </InfoRow>
              <InfoRow>
                <Label>Account Status:</Label>
                <Value>{studentData.status}</Value>
              </InfoRow>
            </StudentInfo>

            {transactions.length > 0 && (
              <TransactionHistory>
                <h3>ðŸ“Š Recent Transactions</h3>
                {transactions.slice(0, 10).map((transaction, index) => (
                  <TransactionItem key={index} type={transaction.type}>
                    <TransactionHeader>
                      <TransactionType type={transaction.type}>
                        {transaction.type}
                      </TransactionType>
                      <TransactionAmount amount={transaction.amount}>
                        {formatAmount(transaction.amount)}
                      </TransactionAmount>
                    </TransactionHeader>
                    <TransactionDate>
                      {formatDate(transaction.date)}
                    </TransactionDate>
                    <div style={{ fontSize: '0.9rem', color: '#666', marginTop: '5px' }}>
                      {transaction.description}
                      {transaction.balance !== null && ` â€¢ Balance: $${transaction.balance}`}
                    </div>
                  </TransactionItem>
                ))}
              </TransactionHistory>
            )}
          </>
        )}
      </Card>
    </Container>
  );
}

export default App;
